import React, {Component} from 'react';
import Header from './Header'
import BookList from './BookList' 
//API_KEY = 'AIzaSyCMf97A53S_uz02WiwQPQQRmq83l-Y0UqY'



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      search: '',
      printType: 'all',
      bookType: ''
    }
  }

  handleSearch = query =>{
    fetch(`https://www.googleapis.com/books/v1/volumes?q=${this.state.search}?printType=${this.state.printType}?filter=${this.state.bookType}`)
      .then(res => res.json())
      .then(books => this.setState({
        search: query
      }));
  }

  componentDidMount() {
    this.setState({ loading: true }); // AJAX request is starting
    fetch(`https://www.googleapis.com/books/v1/volumes?q=${this.state.search}`)
      .then(res => res.json())
      .then(books => this.setState({
        books: books.items
      }));
  }

  render(){
    return (
      <div>
        <Header />
        <BookList books={this.state.books} handleSearch={(e) => this.handleSearch(e)}/>
      </div>
    );
  }
}

export default App;
